import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-start',
  templateUrl: './start.component.html',
  styleUrls: ['./start.component.css']
})
export class StartComponent {
  respuesta :any;

  usuarioSeleccionado: string = 'Candidato';
  correoExistente :any =true;
  comparacionContrasena :boolean =true;
  verErrorContrasena : boolean = true;
  verErrorCorreo : boolean = true;

  correoIngresado: string ="";
  contrasenaIngresada: string ="";

  tipoUsuario = [
    'Administrador',
    'Candidato',
    'empleador',
  ];

  constructor(private router:Router, ){

  }
  ngOnInit(): void {

  }

  evaluarCampos(){

    this.verErrorCorreo= true;
    this.correoExistente= true;
    this.verErrorContrasena=true;
    this.comparacionContrasena =true;
    if(this.correoIngresado =="" && this.contrasenaIngresada==""){
      this.verErrorCorreo= false;
      this.verErrorContrasena=false;
    } else if(this.correoIngresado==""){
      this.verErrorCorreo= false;
    }else if(this.contrasenaIngresada==""){
      this.verErrorContrasena= false;
    }else{
      this.evaluarCorreo();
    }
  }

  evaluarCorreo(){

    const USUARIO = {
      correoElectronico:this.correoIngresado,
      contrasena:this.contrasenaIngresada,
    }
  }


  ingresar(){
    this.router.navigate(['principal']);
  }

  registrar(){

  }
}
<div class="caja superior">
    <div class="cajaIzquierda">
        <div class="card" style="width: 100%; height: 100%; padding: .5rem;">
            <div style="text-align: center; margin-bottom: 2rem;">
                <b>USUARIOS POSTULADOS</b>
                <hr>
            </div>
            <div class="container-md">

                <div class="contenedor">
                  <div class="inicio">

                    <img src="../assets/imagenPre.jpg" class="rounded mx-auto d-block" alt="...">

                    <div class="container-sm" style="padding: 1rem;">

                      <div class="container text-center">
                        <div class="row" style="margin-top: 1rem;margin-bottom: 5px;">
                          <div class="col" style="text-align: left; padding-left: 0%;">
                            Correo:*
                          </div>
                          <div class="col" style="text-align: right; color: red; font-size: 11px;" [ngClass]="{'visualizar':verErrorCorreo, '':!verErrorCorreo}">
                            Campo obligatorio.
                          </div>
                        </div>
                      </div>

                      <div class="row">
                        <div class="col">
                          <input type="text" id="correo" class="form-control" aria-labelledby="passwordHelpBlock"[(ngModel)]="correoIngresado">
                        </div>
                        <div class="col" style="padding-left: 0%;" [ngClass]="{'visualizar':correoExistente, '':!correoExistente}">
                          <div class="alert alert-danger align-items-center" role="alert" style="padding: 4px; height: 38px; width: 14.5rem;">
                            <div style="text-align: center;">
                              Correo no registrado      
                            </div>
                          </div>
                        </div>
                      </div>



                      <div class="container text-center">
                        <div class="row" style="margin-top: .5rem; margin-bottom: 5px;">
                          <div class="col" style="text-align: left; padding-left: 0%;">
                           Contraseña:*
                          </div>
                          <div class="col" style="text-align: right; color: red; font-size: 11px;" [ngClass]="{'visualizar':verErrorContrasena, '':!verErrorContrasena}">
                            Campo obligatorio.
                          </div>
                        </div>
                      </div>


                      <div class="row">
                        <div class="col">
                          <input type="password" id="contraseña" class="form-control" aria-labelledby="passwordHelpBlock"[(ngModel)]="contrasenaIngresada">
                        </div>
                        <div class="col" style="padding-left: 0%;" [ngClass]="{'visualizar':comparacionContrasena, '':!comparacionContrasena}">
                          <div class="alert alert-danger align-items-center" role="alert" style="padding: 4px; height: 38px; width: 14.5rem;">
                            <div style="text-align: center;margin-bottom: 0%;">
                              Contraseña incorrecta      
                            </div>
                          </div>
                        </div>
                      </div>

                      <div style="margin-top: 5px;">
                        <div class="form-check form-check-inline" *ngFor="let usuario of tipoUsuario" style="padding-left: 0;">
                          <input class="form-check-input" type="radio"  name="opciones" [value]="usuario"  style="margin: 10px"[(ngModel)]="usuarioSeleccionado">
                          <label class="form-check-label">{{usuario}}</label>
                        </div>

                      </div>

                      <div style="text-align:center;">
                          <button type="button" class="btn btn-secondary" style="margin-top: 2rem;  width: 10rem;"(click)="evaluarCampos()">Iniciar Sesión</button>
                      </div>

                      <div style="text-align:center; padding-top: 5px;">
                          <p  class ="enlaceRegistro" style="color: blue; text-decoration:underline" (click)="registrar()">Registrar</p>
                      </div>

                    </div>
                  </div>
                </div>   
              </div>
        </div>

    </div>

    <div class="cajaDerecha">

    </div>
</div>
