import { Component, OnDestroy, OnInit } from '@angular/core';
import { Subscription } from 'rxjs';
import { InterfaceService } from 'src/app/Services/InterfaceServices/interface.service';

@Component({
  selector: 'app-alerts',
  templateUrl: './alerts.component.html',
  styleUrls: ['./alerts.component.css']
})
export class AlertsComponent implements OnInit, OnDestroy{
  subscription:Subscription;
  alertas:any=[];

  constructor(private _UserRequest:InterfaceService){
    this.subscription = this._UserRequest.getAlerts().subscribe(data => {
      this.alertas = data;
    });
  }


  ngOnInit(): void {
    this._UserRequest.esparcirAlertas();

  }

  ngOnDestroy(): void {
    this.subscription.unsubscribe();
  }

  cargarPantalla(){
    if(this.alertas.lenght==0){
      return false;
    } else{
      return true;
    }
  }

}
<div style="display: flex;">
    <div class="cajaIzquierda" *ngIf="cargarPantalla(); else PantallaVacia">

        <div class="card" style="width: 100%; margin-top: 1rem;" *ngFor="let notificacion of alertas">
            <div class="tituloVacante" style="height: 2rem;">
                <b></b>
            </div>
            <div style="padding: 3rem;">
                <p style="font-size:medium; padding: 0rem;"> <b>{{notificacion.nombreAlerta}} </b></p>

                <p style="font-size:x-small; line-height: 150%; text-align: justify;">
        {{notificacion.textoAlerta}}
                </p>
            </div>
        </div>
    </div>

    <ng-template #PantallaVacia>
        <p style="text-align: center;">No hay nada aqui</p>
    </ng-template>

    <div class="cajaDerecha" *ngIf="cargarPantalla(); else PantallaVaciaSecunadria">

    </div>

        <ng-template #PantallaVaciaSecunadria>
            <p style="text-align: center;">No hay nada aqui</p>
        </ng-template>

</div>
