import { Component, OnInit } from '@angular/core';
import { EmployerService } from 'src/app/Services/EmployerServices/employer.service';
import { Candidato } from 'src/app/Services/Entity/candidato';
import { Empleador } from 'src/app/Services/Entity/empleador';
import { Vacante } from 'src/app/Services/Entity/vacante';

@Component({
  selector: 'app-candidates',
  templateUrl: './candidates.component.html',
  styleUrls: ['./candidates.component.css']
})
export class CandidatesComponent implements OnInit{

  empleador: Empleador = new Empleador;
  publicaciones: Vacante [] = [];
  candidatos: Candidato[] = [];

  constructor( private _EmployerRequest: EmployerService){

  }

  ngOnInit(): void {
   this.buscarUsuario();
  }

  buscarUsuario() {
    this._EmployerRequest.obtener().then((data: any) => {
      this.empleador = data
      console.log(this.empleador)
    });
  }

  obtenerPublicaciones(){

  }

  cargarPantalla(){
    if(this.publicaciones.length == 0){
      return false;
    } else {
      return true;
    }
  }

  cargarPantallaI(){
    if(this.candidatos.length == 0){
      return false;
    } else {
      return true;
    }
  }



}
<div style="display: flex;">
    <div class="cajaIzquierda">
        <div style="text-align: center; margin-bottom: .5rem; padding-left: 1rem; padding-right: 1rem;">
            <b>CANDIDATOS A LA VACANTE</b>
            <hr>
        </div>
        <div style="padding: 1rem;">
            <div *ngIf="cargarPantallaI(); else pantallaVaciaI">

            </div>

            <ng-template #pantallaVaciaI>
                <p style="text-align: center;">La publicación no tiene Postulantes</p>
            </ng-template>
        </div>
    </div>

    <div class="cajaDerecha">
        <div style="text-align: center; margin-bottom: 2rem; padding-left: 1rem; padding-right: 1rem;">
            <b>VACANTES PUBLICADAS</b>
            <hr>
        </div>
        <div style="padding: 1rem;">
            <div *ngIf="cargarPantalla(); else pantallaVacia">

            </div>

            <ng-template #pantallaVacia>
                <p style="text-align: center;">El empleador no tiene Publicaciones</p>
            </ng-template>
        </div>
    </div>
</div>
