  }



  guardarModPrincipales(usuarioModificado: any) {

    this._CandidateRequest.modificar(usuarioModificado).then((data: any) => {
      if (data.estatus == true) {
        alert("Modificación Exitosa");
        this.router.navigate(['perfil']);

      } else {
        alert("Algo Fallo");
      }
    });

  }



}
