import { Component } from '@angular/core';

@Component({
  selector: 'app-start',
  templateUrl: './start.component.html',
  styleUrls: ['./start.component.css']
})
export class StartComponent {
  viewLog : boolean = false;
  viewCreate : boolean = false; 

  constructor(){

  }

  ngOnInit(){
    this.cambiarMostar();
  }

  cambiarMostar(){
    if(this.viewLog == true){
      this.viewCreate = false;
    } else if(this.viewCreate == true) {
      this.viewLog = false;
    }
  }
}
<div class="caja">
    <div class="cajaIzquierda" >
        <div [ngClass]="{'view':viewLog, '':!viewLog}">
            <app-login (viewLogin)="viewLog = $event"></app-login>
        </div>

    </div>

    <div class="cajaDerecha" >
        <div [ngClass]="{'view':viewCreate, '':!viewCreate}">
            <app-create (viewCreate)="viewCreate = $event"></app-create>
        </div>

    </div>
</div>
