import { Component } from '@angular/core';
import { CandidateService } from 'src/app/Services/CandidateServices/candidate.service';

@Component({
  selector: 'app-jobs',
  templateUrl: './jobs.component.html',
  styleUrls: ['./jobs.component.css']
})
export class JobsComponent {
  imageSeach: string = "../assets/search.png";

  //DEBE SUSCRIBIRSE AL USARIO ENVIADO POR BASE DE DATOS
  usuario:any;

  vacanteSeleccionada:any ="";

  jobsList = [
    {
      id_vacante:1,
      nombreVacante: "Diseñador web",
      especialista: "Diseñador",
      sueldo: 10000,
      horario: "9:00 am - 6:00 pm",
      municipio:{id_municipio:1,nombreMunicipio:"Acolman",id_estado:1},
      estatus: false,
      descripcion: "holaMundo",
      empresa:{id_empresa:1,vacantes:null,nombreEmpresa:"Infotec",descripcion:"holaMundo Infotec"},
      empleador:{
                  id_usuario:1,
                  nombre:"Saumuel",
                  correoElectronico:"mail@gmail.com",
                  consena:"1234hola",
                  tipoUsuario:3,
                  apellidoP:"Aispuro",
                  apellidoM:"Wilson",
                  estatusUsuario:true,
                },
      candidatos:null, // lista de candidatos
      tipoHorario:{id_tipoHorario:1,dias:1},
      tipoContratacion:{id_tipoContratacion:1,horario:1},
      modalidadTrabajo:{},
    }
  ];

  constructor(private _CandidateRequest: CandidateService) {

  }

  ngOnInit() {
    //this.obtenerVcanates();
  }

  obtenerVcanates() {
    this._CandidateRequest.obtenerVacanates().then((data: any) => {
      this.jobsList = data;
    });
  }

  cargarVacantes() {
    if (this.jobsList.length == 0) {
      return false;
    } else {
      return true;
    }
  }

  cargarPantalla(){
    if(this.vacanteSeleccionada== ""){
      return false;
    } else {
      return true;
    }
  }

  postularse(vacante:any){
    const PostDTO = {
      id_candidato:this.usuario.id_candidato,
      id_vacante:vacante.id_vacante
    }
    let postulacion:any ="";
    this._CandidateRequest.postularse(PostDTO).then((data:any) =>{
      postulacion = data;
    });

    if(postulacion != ""){
      alert("Postulacion Exitosa");
    } else {
      alert("Algo Fallo");
    }
  }
}
