import { Component } from '@angular/core';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent {
id_tipoUsusario:number =0;

vacantes:boolean = true;
postulaciones:boolean = true;
notificaciones:boolean = true;
perfil:boolean = true;
administrar:boolean = true;
peticiones:boolean = true;
publicaciones:boolean = true;
publicarNueva:boolean = true;

candidato: any[] = [{ nombrePestana: "Vacantes"},
                    { nombrePestana: "Postulaciones"},
                    { nombrePestana: "Notificaciones"},
                    { nombrePestana: "Perfil"},
];

usuarioVisitante: any[] = [{ nombrePestana: "Vacantes"},
                           { nombrePestana: "Ingresar"},
                           { nombrePestana: "Registrar"},
];

empleador: any[] = [{ nombrePestana: "Publicar Nueva"},
                    { nombrePestana: "Publicaciones"},
                    { nombrePestana: "Notificaciones"},
                    { nombrePestana: "Perfil"},
];

administrador: any[] = [{ nombrePestana: "Administrar"},
                        { nombrePestana: "Peticiones" },
                        { nombrePestana: "Perfil"},
];

windows: any = this.candidato;

constructor(){

}
ngOnInit(){

}

seleccionar(ventana:string){
  if(ventana=="Vacantes"){
    this.vacantes= false;
    alert("Vacantes");
  }else if(ventana=="Postulaciones"){
    this.postulaciones= false;
  }else if(ventana=="Notificaciones"){
    this.notificaciones = false;
  }else if(ventana=="Perfil"){
    this.perfil = false;
  }else if(ventana=="Administrar"){
    this.administrar = false;
  }else if(ventana=="Peticiones"){
    this.peticiones = false;
  }else if(ventana=="Publicaciones"){
    this.publicaciones = false;
  }else if(ventana=="Publicar Nueva"){
    this. publicarNueva = false;
  }else{

  }


}

}


<div style="padding: 2rem;">
        <ul class="nav nav-underline justify-content-end">
            <li class="nav-item">
              <button class="nav-link" href="#" (click)="seleccionar('Vacantes')" [ngClass]="{'visible':vacantes, '':!vacantes}">Vacantes</button>
            </li>
            <li class="nav-item">
                <button class="nav-link" href="#" (click)="seleccionar('Postulaciones')" [ngClass]="{'visible':postulaciones, '':!postulaciones}">Postulaciones</button>
              </li>
              <li class="nav-item">
                <button class="nav-link" href="#" (click)="seleccionar('Notificaciones')" [ngClass]="{'visible':notificaciones, '':!notificaciones}">Notificaciones</button>
              </li>
              <li class="nav-item">
                <button class="nav-link" href="#" (click)="seleccionar('Perfil')" [ngClass]="{'visible':perfil, '':!perfil}">Perfil</button>
              </li>
          </ul>
</div>
