import { Component, Input } from '@angular/core';
import { Empleador } from 'src/app/Services/Entity/empleador';
import { Empresa } from 'src/app/Services/Entity/empresa';
import { Estado } from 'src/app/Services/Entity/estado';
import { ModalidadTrabajo } from 'src/app/Services/Entity/modalidad-trabajo';
import { Municipio } from 'src/app/Services/Entity/municipio';
import { TipoContratacion } from 'src/app/Services/Entity/tipo-contratacion';
import { TipoHorario } from 'src/app/Services/Entity/tipo-horario';
import { Vacante } from 'src/app/Services/Entity/vacante';

@Component({
  selector: 'app-job',
  templateUrl: './job.component.html',
  styleUrls: ['./job.component.css']
})
export class JobComponent {
@Input () vacante : Vacante = new Vacante;

@Input () nombre : string = "";
@Input () especialista: string  = "";
@Input () sueldo: number  = 6223;
@Input () horario: string = "";
@Input () domicilio: string = "";
@Input () descripcion: string  = "";
@Input () verSueldo: string = "";
@Input () empleador: Empleador = new Empleador;
@Input () tipoContratacion: TipoContratacion = new TipoContratacion;
@Input () tipoHorario: TipoHorario = new TipoHorario;
@Input () modalidadTrabajo: ModalidadTrabajo = new ModalidadTrabajo;
@Input () estado: Estado = new Estado;
@Input () municipio: Municipio = new Municipio;
@Input () empresa: Empresa = new Empresa;




constructor(){

}

ngOnInit(){

}

}
<div style="padding: 2rem;">

    <div style="width: 100%;">
        <b style="font-style:normal; font-size:xx-large;">{{vacante.nombreVacante}} {{nombre}} / {{vacante.especialista}} {{especialista}}</b><br>
        <p style="font-size:medium; line-height: 150%; color: darkgray;">
            {{vacante.empresa.nombre}} {{empresa.nombre}}
            <br style="margin: 0%; padding: 0%;">
             {{vacante.municipio.nombreMunicipio}}{{municipio.nombreMunicipio}} Estado de {{vacante.municipio.estado.nombreEstado}}{{estado.nombreEstado}}
            <br style="margin: 0%; padding: 0%;">
            {{vacante.domicilio}} {{domicilio}}
        </p>
    </div>

    <div>
      <div style="padding-top: .5rem;">
        <span class="badge rounded-pill text-bg-secondary">
          <span class="badge rounded-pill text-bg-danger">Danger</span>
        </span>
      </div>
      <div style="padding-top: .5rem;">
        <span class="badge rounded-pill text-bg-secondary">Secondary</span>
      </div>
    </div>

    <div class="container text-left">
        <div class="row">
          <div class="col">
            <div style="display: flex; padding: 0.1rem; padding-bottom:.5rem;">
                <span class="input-group-text altoSueldo" [ngStyle]="{'display':verSueldo}">
                    -  $ {{vacante.sueldo}}.00 mxn / Mensuales
                </span>
                <span class="input-group-text altoSueldo">
                  -  $ {{sueldo}}.00 mxn / Mensuales
               </span>
                <span class="input-group-text bajoSueldo">
                </span>
            </div>
          </div>
          <div class="col">
            <div style="display: flex; padding: 0.1rem; padding-bottom:.5rem;">
                <span class="input-group-text altoHoras">
                    - {{vacante.horario}} {{horario}}
                </span>
                <span class="input-group-text bajoHoras">
                </span>
            </div>
          </div>
        </div>
      </div>

      <div class="container text-left">
        <div class="row">

          <div class="col-4">
            <div style="display: flex; padding: 0.1rem; padding-bottom:.5rem;">
                <span class="input-group-text alto">
                    - {{vacante.tipoHorario.dias}} {{tipoHorario.dias}}
                </span>
                <span class="input-group-text bajo">
                </span>
            </div>
          </div>

          <div class="col-3">
            <div style="display: flex; padding: 0.1rem; margin-left: .5rem; padding-bottom:.5rem;">
                <span class="input-group-text altoModalidad">
                    - {{vacante.modalidadTrabajo.modalidad}} {{modalidadTrabajo.modalidad}}
                </span>
                <span class="input-group-text bajoModalidad">
                </span>
            </div>
          </div>

          <div class="col">
            <div style="display: flex; padding: 0.1rem; margin-left: .8rem; padding-bottom:.5rem;">
                <span class="input-group-text altoContra">
                    - {{vacante.tipoContratacion.horario}} {{tipoContratacion.horario}}
                </span>
                <span class="input-group-text bajoContra">
                </span>
            </div>
          </div>
        </div>
      </div>


      <div style="padding-top: 1rem; text-align: justify;">
        <p style="padding: 0rem">
            {{vacante.descripcion}} {{descripcion}}
        </p>
    </div>

      <div class="container text-center" style="margin-top: 2rem;">
        <div class="row">
          <div class="col" style="font-size:small; line-height: 150%; color: darkgray;">
                Vacante publicada por : {{vacante.empleador.usuario.nombre}}{{empleador.usuario.nombre}} {{vacante.empleador.usuario.apellidoP}}{{empleador.usuario.apellidoP}} {{vacante.empleador.usuario.apellidoM}}{{empleador.usuario.apellidoM}}
                Para mayor información recomendamos contactar al publicante al correo: {{vacante.empleador.usuario.correoElectronico}}{{empleador.usuario.correoElectronico}}.
          </div>
        </div>
      </div>

</div>
