import { Component, OnDestroy, OnInit } from '@angular/core';
import { CandidateService } from 'src/app/Services/CandidateServices/candidate.service';
import { Candidato } from 'src/app/Services/Entity/candidato';
import { InterfaceService } from 'src/app/Services/InterfaceServices/interface.service';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent implements OnInit, OnDestroy {
  usuario: Candidato = new Candidato;
  id_tipoUsuario: number = 0;
  modificarPerfil:boolean=false;

  //varibale de input administrar
  vistaAdministrar: boolean = false;

  perfilTipoAdministrador: boolean = true;
  perfilTipoCandidato: boolean = true;
  perfilTipoEmpleador: boolean = true;
  perfilTipoEmpresa: boolean = true;


  constructor(private _CandidateRequest:CandidateService,private _UserRequest:InterfaceService) {

  }

  ngOnInit(): void {
    this.buscarUsuario();
  }

  ngOnDestroy(): void {
  }

  buscarUsuario(){
    this._CandidateRequest.obtener().then((data:any) =>{
      this.usuario = data
      console.log(this.usuario)
      this.id_tipoUsuario = this.usuario.usuario.tipoUsuario;
      this.identificarTipoDePerfil();
    });
  }

  modificarDatosPerfil(){

    if(this.modificarPerfil == false){
      this.modificarPerfil = true;
    }else {
      this.modificarPerfil = false;
    }

  }

  identificarTipoDePerfil() {
    if (this.id_tipoUsuario == 0) {

      this.perfilTipoAdministrador = false;
      this.perfilTipoCandidato = true;
      this.perfilTipoEmpleador = true;
      this.perfilTipoEmpresa = true;

    } else if (this.id_tipoUsuario == 2) {

      this.perfilTipoAdministrador = true;
      this.perfilTipoCandidato = false;
      this.perfilTipoEmpleador = true;
      this.perfilTipoEmpresa = true;

    } else if (this.id_tipoUsuario == 3) {

      this.perfilTipoAdministrador = true;
      this.perfilTipoCandidato = true;
      this.perfilTipoEmpleador = false;
      this.perfilTipoEmpresa = true;

    } else {

      this.perfilTipoAdministrador = true;
      this.perfilTipoCandidato = true;
      this.perfilTipoEmpleador = true;
      this.perfilTipoEmpresa = false;
    }
  }

  cerrarSesion(){
    this._UserRequest.hacerVisitante();
    this._UserRequest.ocultarNavB();
    this._UserRequest.guaradarCorreo("");
  }


}
<div style="display: flex;">
    <div class="cajaIzquierda">
        <!--  -->
        <div class="card" style="width: 100%; height: 100%; padding: .5rem;">
            <div style=" position: relative;">
                <div class="bajo" >
                    <div class="card text-bg-dark" style="height: 150px;">
                        <img src="..." class="card-img" alt="..." style="height: 150px;">
                        <div class="card-img-overlay">
                          <h5 class="card-title" style="text-align: right;" 
                          [ngClass]="{'view':perfilTipoCandidato, '':!perfilTipoCandidato}">FaltaPuesto</h5>
                        </div>
                    </div>
                    <div class="sobre">
                        <img src="..." class="img-thumbnail" alt="..." style="height: 100px; width: 100px; margin-left: 1rem;">
                    </div>
                </div>
            </div>

            <div class="info">
                <h6 style="margin-right: .3rem;" [ngClass]="{'':perfilTipoEmpresa, 'view':!perfilTipoEmpresa}">{{usuario.usuario.nombre}} {{usuario.usuario.apellidoP}} {{usuario.usuario.apellidoM}}</h6>
                <!-- <h6 style="margin-right: .3rem;" [ngClass]="{'view':perfilTipoEmpresa, '':!perfilTipoEmpresa}">{{usuario.nombre}}</h6> -->
                <h6 style="color: darkgray; font-style: oblique;" [ngClass]="{'view':perfilTipoCandidato, '':!perfilTipoCandidato}"> ({{usuario.edad}})</h6>
            </div>
            <div class="infoCuerpo">

                <!-- <p style="font-size:x-small; line-height: 150%;" [ngClass]="{'view':perfilTipoEmpresa, '':!perfilTipoEmpresa}">
                    {{usuario.domicilio}}
                    <br style="margin: 0%; padding: 0%;">
                    {{usuario.municipio.nombreMunicipio}} Estado de {{usuario.estado.nombreEstado}}
                </p> -->

                <p style="font-size:x-small; line-height: 150%;" [ngClass]="{'view':perfilTipoCandidato, '':!perfilTipoCandidato}">
                    Centro Educativo: {{usuario.centroEducativo}}
                    <br style="margin: 0%; padding: 0%;">
                    Actualmente laborando como: {{usuario.puestoActual}}
                </p>

                <p style="font-size:x-small; line-height: 150%;" [ngClass]="{'view':perfilTipoCandidato, '':!perfilTipoCandidato}">
                    {{usuario.domicilio}}
                    <br style="margin: 0%; padding: 0%;">
                    {{usuario.municipio.nombreMunicipio}} Estado de {{usuario.estado.nombreEstado}}
                </p>

                <p style="font-size:x-small; line-height: 150%;" [ngClass]="{'view':perfilTipoCandidato, '':!perfilTipoCandidato}"> 
                    {{usuario.usuario.correoElectronico}}
                    <br style="margin: 0%; padding: 0%;">
                    Tel: {{usuario.usuario.telefono}}
                </p>

                <!-- <p style="font-size:x-small; line-height: 150%;" [ngClass]="{'':perfilTipoEmpresa, 'view':!perfilTipoEmpresa}"> 
                    {{usuario.correoElectronico}}
                    <br style="margin: 0%; padding: 0%;">
                    Tel: {{usuario.telefono}}
                </p> -->

                <b [ngClass]="{'view':perfilTipoCandidato, '':!perfilTipoCandidato}">Descripción</b>
                <p style="font-size:x-small; line-height: 150%; text-align: justify;" 
                [ngClass]="{'view':perfilTipoCandidato, '':!perfilTipoCandidato}">
                {{usuario.descripcion}}
                </p>

                <!-- <b [ngClass]="{'view':perfilTipoEmpresa, '':!perfilTipoEmpresa}">Descripción</b>
                <p style="font-size:x-small; line-height: 150%; text-align: justify;" 
                [ngClass]="{'view':perfilTipoEmpresa, '':!perfilTipoEmpresa}">
                {{usuario.descripcion}}
                </p> -->

            </div>
            <div class="infoCuerpo" [ngClass]="{'view':perfilTipoCandidato, '':!perfilTipoCandidato}">
                <!-- SE CAMBIARA POR UN NGFOR DE LAS HABILIDADES DEL USUARIO REGISTRADAS EN BASE DE DATOS -->
                <span class="badge rounded-pill text-bg-secondary" style="margin-right: .5rem;margin-bottom: .5rem;">habilidad</span>
            </div>
        </div>
        <div [ngClass]="{'view':vistaAdministrar, '':!vistaAdministrar}">
            <button type="button" class="btn btn-warning" style="width: 100%; margin-top: 1rem;" [ngClass]="{'':perfilTipoEmpresa, 'view':!perfilTipoEmpresa}" (click)="modificarDatosPerfil()">MODIFICAR CUENTA</button>
            <button type="button" class="btn btn-danger" style="width: 100%; margin-top: 1rem;" [ngClass]="{'':perfilTipoEmpresa, 'view':!perfilTipoEmpresa}" (click)="cerrarSesion();">CERRAR SESIÓN</button>
            <button type="button" class="btn btn-danger" style="width: 100%; margin-top: 1rem;" [ngClass]="{'':perfilTipoEmpresa, 'view':!perfilTipoEmpresa}">SUSPENDER CUENTA</button>
        </div>

        <!--  -->
    </div>


    <div class="cajaDerecha">
        <app-update [ngClass]="{'':modificarPerfil, 'view':!modificarPerfil}"></app-update>
    </div>
</div>
