<div style="padding: 2rem;" [ngClass]="{'activar':candidatoActivo, '':!candidatoActivo}">
        <ul class="nav nav-underline justify-content-end">
            <li class="nav-item">
              <button class="nav-link" href="#" (click)="seleccionar('Vacantes')" [ngClass]="{'':vacantes, 'active':!vacantes}">Vacantes</button>
            </li>
            <li class="nav-item">
                <button class="nav-link" href="#" (click)="seleccionar('Postulaciones')" [ngClass]="{'':postulaciones, 'active':!postulaciones}">Postulaciones</button>
              </li>
              <li class="nav-item">
                <button class="nav-link" href="#" (click)="seleccionar('Notificaciones')" [ngClass]="{'':notificaciones, 'active':!notificaciones}">Notificaciones</button>
              </li>
              <li class="nav-item">
                <button class="nav-link" href="#" (click)="seleccionar('Perfil')" [ngClass]="{'':perfil, 'active':!perfil}">Perfil</button>
              </li>
          </ul>
</div>
<!-- VISITANTE -->
<div style="padding: 2rem;" [ngClass]="{'activar':visitanteActivo, '':!visitanteActivo}">
    <ul class="nav nav-underline justify-content-end">
        <li class="nav-item">
          <button class="nav-link" href="#" (click)="seleccionar('Vacantes')" [ngClass]="{'':vacantes, 'active':!vacantes}">Vacantes</button>
        </li>
        <li class="nav-item">
            <button class="nav-link" href="#" (click)="seleccionar('Login')" [ngClass]="{'':postulaciones, 'active':!postulaciones}">Login</button>
        </li>
      </ul>
</div>
<!-- EMPLEADOR -->
<div style="padding: 2rem;" [ngClass]="{'activar':empleadorActivo, '':!empleadorActivo}">
    <ul class="nav nav-underline justify-content-end">
        <li class="nav-item">
          <button class="nav-link" href="#" (click)="seleccionar('Publicar Nueva')" [ngClass]="{'':publicarNueva, 'active':!publicarNueva}">Publicar Nueva</button>
        </li>
        <li class="nav-item">
            <button class="nav-link" href="#" (click)="seleccionar('Publicaciones')" [ngClass]="{'':publicaciones, 'active':!publicaciones}">Publicaciones</button>
          </li>
          <li class="nav-item">
            <button class="nav-link" href="#" (click)="seleccionar('Notificaciones')" [ngClass]="{'':notificaciones, 'active':!notificaciones}">Notificaciones</button>
