import { Component } from '@angular/core';
import { CandidateService } from 'src/app/Services/CandidateServices/candidate.service';

@Component({
  selector: 'app-jobs',
  templateUrl: './jobs.component.html',
  styleUrls: ['./jobs.component.css']
})
export class JobsComponent {
  imageSeach: string = "../assets/search.png";

  jobsList = [
    {
      nombreVacante: "Dise単ador web",
      especialista: "Dise単ador",
      sueldo: 10000,
      horario: "9:00 am - 6:00 pm",
      municipio:{id_municipio:1,nombreMunicipio:"Acolman",id_estado:1},
      estatus: false,
      descripcion: "holaMundo",
      empresa:{id_empresa:1,vacantes:null,nombreEmpresa:"Infotec",descripcion:"holaMundo Infotec"},
      empleador:{},
      candidatos:null, // lista de candidatos
      tipoHorario:{id_tipoHorario:1,dias:1},
      tipoContratacion:{id_tipoContratacion:1,horario:1},
      modalidadTrabajo:{},


    }
  ];

  constructor(private _CandidateRequest: CandidateService) {

  }

  ngOnInit() {
    //this.obtenerVcanates();
  }

  obtenerVcanates() {
    this._CandidateRequest.obtenerVacanates().then((data: any) => {
      this.jobsList = data;
    });
  }

  cargarVacantes() {

    if (this.jobsList.length == 0) {
      return false;
    } else {
      return true;
    }
  }
}
